INSERT INTO `cms_article` VALUES ('89', '新型冠状病毒', '<span style=\"background-color:#FFFFFF;color:#222222;font-family:\" font-size:16px;\"=\"\">2月10日，习近平总书记在北京调研指导新型冠状病毒肺炎疫情防控工作。新冠肺炎疫情发生以来，习近平高度重视，多次召开会议、多次听取汇报、作出重要指示，亲自指挥、亲自部署疫情防控阻击战。“第一位”“一盘棋”“第一线”，打赢疫情防控阻击战，习近平强调这三个“一”。</span>', '/pic/image/20200212/20200212214726_534.jpg', '3', '10', '190', '2', '0', '1', '0', '2020-02-12 21:48:14', '2020-02-12 22:04:10', null, '0');
